import React from 'react';
import './Project.css'; // This is where you would import your CSS from

// Card Component
const Card = ({ title, description, imageUrl, buttonText }) => {
  return (
    <div className="card">
      <img src={imageUrl} alt={title} className="card-image" />
      <div className="card-content">
        <h2 className="card-title">{title}</h2>
        <p className="card-description">{description}</p>
        <button className="card-button">{buttonText}</button>
      </div>
    </div>
  );
};

// Main Component where cards are used
const CardsList = () => {
  const cardsData = [
    {
      title: 'The Everyday Salad',
      description: 'Take your boring salads up a knotch. This recipe is perfect for lunch and only contains 5 ingredients!',
      imageUrl: 'path-to-salad-image.jpg',
      buttonText: 'View Recipe'
    },
    {
      title: 'Simple Risotto',
      description: 'Fear Risotto no more! This simple recipe is perfect for family dinners.',
      imageUrl: 'path-to-risotto-image.jpg',
      buttonText: 'View Recipe'
    },
    {
      title: 'Baked Cod with Vegetables',
      description: 'Baked Cod with Vegetables. 30 minute meal!',
      imageUrl: 'path-to-cod-image.jpg',
      buttonText: 'View Recipe'
    }
  ];

  return (
    <div className="cards-list">
      {cardsData.map((card, index) => (
        <Card key={index} {...card} />
      ))}
    </div>
  );
};

export default CardsList;
