import React, { useState } from "react";
import "./Navbar.css";
import { Link } from "react-scroll";
import Expand from "@iconscout/react-unicons/icons/uil-align-justify";
import Close from "@iconscout/react-unicons/icons/uil-times";
import DK from "../../img/dk.png";

function Navbar() {
  const [show, setShow] = useState(false); // Start with the menu collapsed
  const handleClick = () => {
    setShow(!show);
  };

  const handleLinkClick = () => {
    setShow(false); // Collapse the menu when a link is clicked
  };

  return (
    <>
      <div className="n-wrapper">
        <div className="n-left">
          <div className="n-name">
            <img src={DK} alt="Deep" height={60} width={60} />
          </div>
          <button
            id="menu"
            type="button"
            className={`n-expand n-expand-animate ${show ? "open" : ""}`}
            onClick={handleClick}
          >
            {show ? (
              <Close color="orange" size="2rem" />
            ) : (
              <Expand color="orange" size="2rem" />
            )}
          </button>
        </div>
        <div className="n-right">
          <div className={show ? "n-list showed" : "n-list"}>
            <ul style={{ listStyleType: "none" }}>
              <Link
                spy={true}
                to="Navbar"
                smooth={true}
                activeClass="activeClass"
                onClick={handleLinkClick} // Ensure onClick is attached here
              >
                <li>Home</li>
              </Link>
              <Link
                spy={true}
                to="Education"
                smooth={true}
                onClick={handleLinkClick}
              >
                {" "}
                // Attach onClick here
                <li>Education</li>
              </Link>
              <Link
                spy={true}
                to="Services"
                smooth={true}
                onClick={handleLinkClick}
              >
                {" "}
                // And here
                <li>Skills</li>
              </Link>
              <Link
                spy={true}
                to="Experience"
                smooth={true}
                onClick={handleLinkClick}
              >
                {" "}
                // And here
                <li>Experience</li>
              </Link>
              <Link
                spy={true}
                to="Portfolio"
                smooth={true}
                onClick={handleLinkClick}
              >
                {" "}
                // And here
                <li>Portfolio</li>
              </Link>
            </ul>
          </div>
          <Link spy={true} to="Contact" smooth={true} onClick={handleLinkClick}>
            {" "}
            // And finally here
            <button className="button n-button">Contact</button>
          </Link>
        </div>
      </div>
    </>
  );
}

export default Navbar;
