import React from "react";
import "./Education.css";
import { motion } from "framer-motion/dist/framer-motion";
import { useInView } from "react-intersection-observer";
import Carleton from "../../img/carleton.png";
import Gtu from "../../img/gtu.png";
import Gseb from "../../img/Gseb.png";
const Education = () => {
  const transition = { duration: 2, type: "spring" };
  const events = [
    {
      title: "Carleton University",
      description: "Description of event 1",
      degree: "Master of Engineering: Electrical and Computer",
      Grade: "GPA : 10.6/12",
      date: "2021-01-01",
      imageurl: Carleton,
    },
    {
      title: "Gujarat Technological University",
      description: "Description of event 2",
      degree: "Bachelor of Engineering - Information Technology",
      Grade: "GPA 3.84 / 4",
      date: "2021-02-01",
      imageurl: Gtu,
    },
    {
      title: "Gujarat Higher Secondary Eduacation Board",
      description: "Description of event 2",
      degree: "",
      Grade: "",
      date: "2021-02-01",
      imageurl: Gseb,
    },
    {
      title: "Gujarat Secondary Eduacation Board",
      description: "Description of event 2",
      degree: "",
      Grade: "",
      date: "2021-02-01",
      imageurl: Gseb,
    },
    // Add more events as needed
  ];

  return (
    <div className="timeline-container">
      {events.map((event, index) => {
        const [ref, inView] = useInView({
          threshold: 0.5, // Trigger when 50% of the item is in view
        });

        return (
          <div className="Edu">
            <span>Hey! I am</span>
            <span>D K Patel</span>
            <motion.div
              ref={ref}
              className="timeline-item"
              key={index}
              initial={{ x: -100, opacity: 0 }}
              animate={inView ? { x: 0, opacity: 1 } : ""}
              transition={{ duration: 0.5, delay: index * 0.2 }}
            >
              <div className="timeline-dot">
                <img src={event.imageurl} alt="" width={70} height={70} />
              </div>
              <div className="timeline-content">
                <h3>{event.title}</h3>
                <p>{event.description}</p>
                <span>{event.date}</span>
              </div>
            </motion.div>
          </div>
        );
      })}
    </div>
  );
};

export default Education;
