import React, { useState } from "react";
import Toggle from "../Toggle/Toggle";
import "./Navbar.css";
import { Link } from "react-scroll";
import Expand from "@iconscout/react-unicons/icons/uil-align-justify";

function Navbar() {
  const [show, setShow] = useState(true);
  const handleClick = () => {
    setShow(!show);
  };

  if (show)
    window.$(document).ready(function() {
      window.$("#menu").click(function() {
        window.$("#collapse").slideToggle("slow");
      });
    });
  return (
    <>
      <div className="n-wrapper">
        <div className="n-left">
          <div className="n-name">Deep</div>
          <Toggle />
          <button id="menu" type="button" onClick={handleClick}>
            <Expand color="orange" size="2rem" />
          </button>
        </div>
        <div className="n-right">
          <div
            className="n-list"
            id="collap"
          >
            <ul style={{ listStyleType: "none" }}>
              <Link
                spy={true}
                to="Navbar"
                smooth={true}
                activeClass="activeClass"
              >
                <li>Home</li>
              </Link>

              <Link spy={true} to="Services" smooth={true}>
                <li>Services</li>
              </Link>

              <Link spy={true} to="Experience" smooth={true}>
                <li>Experience</li>
              </Link>

              <Link spy={true} to="Portfolio" smooth={true}>
                <li>Portfolio</li>
              </Link>

              <Link spy={true} to="Review" smooth={true}>
                <li>Review</li>
              </Link>
            </ul>
          </div>
          <Link spy={true} to="Contact" smooth={true}>
            <button className="button n-button">Contact</button>
          </Link>
        </div>
      </div>
    </>
  );
}

export default Navbar;
